
import {  Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
const SlideShow = () => {
    const slideImages = [
        {
          url: 'data:image/jpeg;base64,',
          caption: 'Banderban'
        },
        {
          url: 'data:image/jpeg;base64,',
          caption: "Cox's Bazar"
        },
        {
          url: 'data:image/jpeg;base64,',
          caption: 'Sundarban'
        },
      ];
    return (
        <div>
               <Slide className="">
         {slideImages.map((slideImage, index)=> (
            <div className="each-slide" key={index}>
              <div style={{'backgroundImage': `url(${slideImage.url})`}} className='h-[600px] bg-no-repeat bg-cover'>
                <div className='flex justify-center items-center text-4xl font-body font-bold'>
                <span>{slideImage.caption}</span>
                </div>
              </div>
            </div>
          ))} 
        </Slide>
        </div>
    );
};

export default SlideShow;